INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_h1`, `meta_description`, `meta_keyword`) VALUES ('79', '3', 'Шорты pole dance со стропами бифлекс черные', '&lt;p&gt;Шорты для зажигательных танцев на пилоне. Изготовлены из бифлекса, а потому плотно прилегают к телу, но в то же время хорошо тянутся. Шортики формы танга с гартерами подчеркнут округлость ягодиц. Шорты не выстирываются и не выцветают. Посадка средняя. В классическом черном цвете. Добавьте яркости вашему танцу благодаря невероятным шортам от Heaven!&lt;br&gt;&lt;/p&gt;', '', 'Шорты со стропами бифлекс черные', '', '', '');
